import React from 'react'
import {useParams} from 'react-router-dom'
import axios from 'axios';
import { useState } from 'react';
export const Marvel = () => {
  const {id}=useParams();
  const [item,setItem]=useState()
  const fetch=async()=>{
    const res=await axios.get(`https://gateway.marvel.com:443/v1/public/characters/${id}?ts=1&apikey=1d7cfe54e852e746253965905cc92c15&hash=b4db0c59e3398735fc741b5da60f1ba1`)
    setItem(res.data.data.results[0])
  }
  fetch();

  function NonIdiomaticList(props) {
    // Build an array of items
    let array = [];
    for (let i = 0; i < props.items.length; i++) {
      array.push(
          <Item key={i} item={props.items[i]}/>
      );
    }
  }

  return (
    <>
    {
      (!item)? "":(
        <div className="box-content">
          <div className="right-box">
          <img src={`${item.thumbnail.path}.${item.thumbnail.extension}`} alt="" />
          </div>
          <div className="left-box">
            <h1>{item.name}</h1>
            <h4>{item.description}</h4>
          </div>
        </div>
      )
    }
    </>
  )
}
