import React from "react"
import { Character } from "./Character"
import axios from "axios"
import { useState } from "react"
import { useEffect } from "react"
import Carousel from '@mui/material'
import { Paper, Button } from '@mui/material'

import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

export const Main = () => {
  const [url,setUrl]=useState("http://gateway.marvel.com/v1/public/characters?ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a")
  const [item,setItem]=useState();
  const [search,setSearch]=useState("");
  useEffect(()=>{
    const fetch=async()=>{
      const res=await axios.get(url)
      setItem(res.data.data.results);
    }
    fetch();
  },[url])

  const searchMarvel=()=>{
    setUrl(`https://gateway.marvel.com:443/v1/public/characters?nameStartsWith=${search}&ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a`)
  }

    return (
        <>

            <div className="header">
                <div className="bg">
                    <img src="./Images/background.jpg" alt="" />
                </div>
                <div className="search-bar">
                    <img src="./Images/logo.png" alt="logo"/>
                    <Box
                        sx={{
                            width: 500,
                            maxWidth: '100%',
                            marginLeft: 50,
                        }}
                    >
                        <TextField fullWidth label="Search" id="Search" onChange={e=>setSearch(e.target.value)} onKeyPress={searchMarvel}/>
                    </Box>
                </div>
            </div>
            <div className="content">

                {
                    (!item)?<p><code>Characters not found</code></p>:<Character data={item}/>
                }
            </div>
        </>
    )
}

function Example(props)
{
    var items = [
        {
            name: "Random Name #1",
            description: "Probably the most random thing you have ever seen!"
        },
        {
            name: "Random Name #2",
            description: "Hello World!"
        }
    ]

    return (
        <Carousel>
            {
                items.map( (item, i) => <Item key={i} item={item} /> )
            }
        </Carousel>
    )
}

function Item(props)
{
    return (
        <Paper>
            <h2>{props.item.name}</h2>
            <p>{props.item.description}</p>

            <Button className="CheckButton">
                Check it out!
            </Button>
        </Paper>
    )
}
